targets.expr:
((avg by (job,kafka_cluster) (
  sum without (id) (jvm_memory_bytes_used{area="heap", job="integrations/kafka",job=~"$job",kafka_cluster=~"$kafka_cluster",instance=~"$instance"})
))/(avg by (job,kafka_cluster) (
  sum without (id) (jvm_memory_bytes_max{area="heap", job="integrations/kafka",job=~"$job",kafka_cluster=~"$kafka_cluster",instance=~"$instance"} != -1)
))) * 100

